import React, { useState, useEffect } from "react";
import { Link, Navigate, NavLink } from "react-router-dom";
import axios from "axios";
import Header from "./Header";
import "./bgstatic.css";

const myStyle = {
    backgroundImage: 'url("/bg.jpg"})',
    height: "100vh",
    marginTop: "-70px",
    fontSize: "50px",
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
  };

const LandinPage = () => {
  const [search, setSearch] = useState(null);
  const [data, setData] = useState([]);
  const [tokenn, setTokenn] = useState(localStorage.getItem("token"));
  const [y, setY] = useState("");
  
  if (!localStorage.getItem("token")) {
    return <Navigate to="/login" />;
  }
  console.log(tokenn);

  return (
    <div className="first">
      <Header />

      <section className="container">
        <center>
          {" "}
          <h1
            className="large "
            style={{ color: "#30332E", marginTop: "20px" }}
          >
            Welcome to Venture Hub
          </h1>
        </center>

        <nav className="navbar navbar-light">
          <div className="container-fluid">
            <center>
                <h3>
                    A student acivity website made by students for students.
                </h3>
                <li className="nav-link ">
                <NavLink to="/roommatesearch" className="nav-link" style={({ isActive }) => ({ color: isActive ? '#56CEDB' : '#FFFDFB' })}>
                    Click here to browse for roommates
                </NavLink>
            </li>
            <li className="nav-link ">
                <NavLink to="/eventsearch" className="nav-link" style={({ isActive }) => ({ color: isActive ? '#56CEDB' : '#FFFDFB' })}>
                    Click here to browse events
                </NavLink>
            </li>
            <li className="nav-link ">
                <NavLink to="/roommatesearch" className="nav-link" style={({ isActive }) => ({ color: isActive ? '#56CEDB' : '#FFFDFB' })}>
                    Roommate Search
                </NavLink>
            </li>
            {/* <li className="nav-link ">
                <NavLink to="/registeredcompanies" className="nav-link" style={({ isActive }) => ({ color: isActive ? '#56CEDB' : '#FFFDFB' })}>
                    Your Events
                </NavLink>
            </li> */}
            <li className="nav-link ">
                <NavLink to="/addcompany" className="nav-link" style={({ isActive }) => ({ color: isActive ? '#56CEDB' : '#FFFDFB' })}>
                    Create Events
                </NavLink>
            </li>
            <li className="nav-link ">
                <NavLink to="/roommatepost" className="nav-link" style={({ isActive }) => ({ color: isActive ? '#56CEDB' : '#FFFDFB' })}>
                    Post for Roommates
                </NavLink>
            </li>
            <li className="nav-link">
                <NavLink to="/login" className="btn btn-secondary" >
                    Logout
                </NavLink>
            </li>
            </center>
          </div>
        </nav>

      </section>

      {tokenn === "undefined" && <Navigate to="/login" />}
    </div>
  );
};

export default LandinPage;
