import React, { useState } from "react";
import axios from "axios";

const ForgotPassword = () => {
  const [data, seData] = useState({
    email: "",
  });
  const { email } = data;

  const changeHandler = (e) => {
    seData({ ...data, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5000/sendLoginCredentials", data)
      .then((res) => {
        if (res.data.length <= 30) {
          console.error("Error:", error.response.data.message);
          alert("Error sending login credentials. Please try again later.");
        }
      });
    alert("Login credentials sent to your email");
  };

  return (
    <div>
      <h2>Enter your email to receive login credentials</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          value={email}
          onChange={changeHandler}
          placeholder="Enter your email"
          required
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default ForgotPassword;
