import React, { useState, useEffect } from 'react';
import { Link, Navigate } from 'react-router-dom';
import axios from 'axios';
import Header from './Header';
import './bg.css';

const RoommateSearch = () => {
    const [search, setSearch] = useState(null);
    const [data, setData] = useState([]);
    const [tokenn, setTokenn] = useState(localStorage.getItem('token'));
    const [y, setY] = useState('');

    useEffect(() => {
        axios.get('http://localhost:5000/getrooms').then(res =>
            !search ? setData(res.data) : setData(res.data.filter(rooms =>
                rooms.priceRange.includes(search.toUpperCase()) ||
                rooms.moveInDate.toLowerCase().includes(search.toLowerCase()) ||
                rooms.gender.toLowerCase().includes(search.toLowerCase())
            ))
        );

        // axios.get('http://localhost:5000/getpresentuser', {
        //     headers: {
        //         'x-token': localStorage.getItem('token')
        //     }
        // }).then(
        //     res => setY(res.data._id)
        // );
    }, [search]);

    if (!localStorage.getItem('token')) {
        return <Navigate to='/login' />;
    }

    const searchHandler = e => {
        e.preventDefault();
        axios.get('http://localhost:5000/getrooms').then(res =>
            !search ? setData(res.data) : setData(res.data.filter(rooms =>
                rooms.priceRange.includes(search.toUpperCase()) ||
                rooms.moveInDate.toLowerCase().includes(search.toLowerCase()) ||
                rooms.gender.toLowerCase().includes(search.toLowerCase())
            ))
        );
    };

    return (
        <div className='first'>
            <Header />

            <section className="container">
                <center><h1 className="large" style={{ "color": "#30332E", "marginTop": "20px" }}>Discover new events</h1></center>

                <nav className="navbar navbar-light">
                    <div className="container-fluid">
                        <h3 className="navbar-brand">Search for Rooms and Roommates<span style={{ color: "blue" }}> </span></h3>
                    </div>
                </nav>

                <div className="Roommates">
                    <div className="row">
                        {data.length >= 1 ?
                            data.map(rooms =>
                                <div className='col-md-4'>
                                    <div className="Roommates bg-light card" style={{ "margin": "10px", "width": "25.5rem" }}>
                                        <center>
                                            <div>
                                                <h2 style={{ "color": "#6DAFED" }}>Posted by: {rooms.name}</h2>
                                                <h6>Contact: {rooms.contact}</h6>
                                                <h6>Move in Date: {rooms.gender}</h6>
                                                <h6>Gender: {rooms.moveInDate}</h6>
                                                <h6>Price: {rooms.priceRange}</h6>
                                            </div>
                                        </center>
                                    </div>
                                </div>
                            )
                            : null}
                    </div>
                </div>
            </section>

            {tokenn === "undefined" && <Navigate to="/login" />}
        </div>
    );
}

export default RoommateSearch;
