import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from './Header';

const TextbookSearch = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const [token, setToken] = useState(localStorage.getItem('token'));

  useEffect(() => {
    if (!token) {
      // Redirect to login if token is not present
      // Assuming you have a Login component with the route '/login'
      window.location.href = '/login';
    }
  }, [token]);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`/search/${searchQuery}`);
      setSearchResults(response.data);
      setErrorMessage('');
    } catch (error) {
      setErrorMessage('An error occurred while searching for textbooks.');
    }
  };

  return (
    <div className="first">
      <Header />
      <section className="container">
        <center>
          <h1 className="large" style={{ color: '#30332E', marginTop: '20px' }}>
            Textbook Search
          </h1>
        </center>

        <div>
          <input
            type="text"
            placeholder="Search for textbooks"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button onClick={handleSearch}>Search</button>
        </div>

        {errorMessage && <p>{errorMessage}</p>}

        <div className="textbookResults">
          {searchResults.map((textbook, index) => (
            <div key={index} className="textbookCard">
              <h3>{textbook.title}</h3>
              <p>Author: {textbook.author}</p>
              <p>ISBN: {textbook.isbn}</p>
              {textbook.available ? (
                <p>Location: {textbook.location}</p>
              ) : (
                <p>Available at: {textbook.bookstore}</p>
              )}
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default TextbookSearch;
