import React, { useState } from 'react';
import { FormControl, InputLabel, Select, MenuItem, Button, makeStyles } from '@material-ui/core';
import { Link } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
  formContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    minHeight: '100vh', // Ensure the form is centered vertically on the page
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  button: {
    margin: theme.spacing(1),
  },
}));

const VoteForm = () => {
  const classes = useStyles();
  const [candidate, setCandidate] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/vote', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ candidate }),
      });
      const data = await response.json();
      console.log(data);
      alert('Your vote has been registered successfully!');
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className={classes.formContainer}>
      <h2>Voting</h2>
      <br/>
      <form onSubmit={handleSubmit}>
        <FormControl className={classes.formControl}>
          <InputLabel>Vote for</InputLabel>
          <Select value={candidate} onChange={(e) => setCandidate(e.target.value)}>
            <MenuItem value="">
              <em>Select candidate</em>
            </MenuItem>
            <MenuItem value="John">John</MenuItem>
            <MenuItem value="Mary">Mary</MenuItem>
            <MenuItem value="Susan">Susan</MenuItem>
          </Select>
        </FormControl>
        <br/>
        <br/>
        <Button type="submit" variant="contained" color="primary" className={classes.button}>
          Submit
        </Button>
      </form>
      <Button component={Link} to="/dashboard" variant="outlined" color="primary" className={classes.button}>
        Back to Dashboard
      </Button>
    </div>
  );
};

export default VoteForm;
