import React, { useState } from 'react';
import { NavLink, Navigate } from 'react-router-dom';
import axios from 'axios';
import { Container, Typography, TextField, Button, FormControl, InputLabel, Select, MenuItem } from '@mui/material';
import './App.css';
import './bgstatic.css';

const EditProfile = () => {
    const [data, setData] = useState({
        firstName: '',
        lastName: '',
        address: '',
        city: '',
        state: '',
        zipCode: '',
        department: '',
        phoneNumber: '',
        loginName: '',
        password: '',
        confirmPassword: ''
    });
    const [x, setX] = useState(0);
    const { firstName, lastName, address, city, state, zipCode, department, phoneNumber, loginName, password, confirmPassword } = data;

    const changeHandler = e => {
        setData({ ...data, [e.target.name]: e.target.value });
    };

    const submitHandler = e => {
        e.preventDefault();
        if (password.length > 5) {
            if (firstName && lastName && loginName && department && phoneNumber) {
                if (password === confirmPassword) {
                    if (address && city && state && zipCode) {
                        axios.post('http://localhost:5000/editprofile', data, {
                            headers: {
                                'x-token': localStorage.getItem('token')
                            }
                        }).then(res => {
                            setX(x + 1);
                            alert(res.data);
                            console.log(x);
                        });
                    } else {
                        alert("Please enter a valid address");
                    }
                } else {
                    alert("Passwords do not match");
                }
            } else {
                alert("Please fill the complete form with valid details");
            }
        } else {
            alert("Please use min 6 characters for password");
        }
    };

    return (
        <div className='back'>
            <nav className="navbar bg-dark justify-content-left">
                <Typography variant="h1" id="header">
                    Venture Hub
                </Typography>
            </nav>
            <Container>
                <Typography variant="h2" className="large" style={{ color: "#30332E", marginTop: "50px" }}>Edit Profile</Typography>
                <Typography variant="subtitle1" className="lead"><b>Please enter your details carefully</b></Typography>
                <form onSubmit={submitHandler}>
                    <TextField fullWidth variant="outlined" margin="normal" label="First Name*" onChange={changeHandler} value={firstName} name="firstName" />
                    <TextField fullWidth variant="outlined" margin="normal" label="Last Name*" onChange={changeHandler} value={lastName} name="lastName" />
                    <TextField fullWidth variant="outlined" margin="normal" label="Address*" onChange={changeHandler} value={address} name="address" />
                    <TextField fullWidth variant="outlined" margin="normal" label="City*" onChange={changeHandler} value={city} name="city" />
                    <TextField fullWidth variant="outlined" margin="normal" label="State*" onChange={changeHandler} value={state} name="state" />
                    <TextField fullWidth variant="outlined" margin="normal" label="Zip Code*" onChange={changeHandler} value={zipCode} name="zipCode" />
                    <TextField fullWidth variant="outlined" margin="normal" label="Department*" onChange={changeHandler} value={department} name="department" />
                    <TextField fullWidth variant="outlined" margin="normal" label="Phone Number*" onChange={changeHandler} value={phoneNumber} name="phoneNumber" />
                    <TextField fullWidth variant="outlined" margin="normal" label="Username*" onChange={changeHandler} value={loginName} name="loginName" />
                    <TextField fullWidth variant="outlined" margin="normal" type="password" label="Password*" onChange={changeHandler} value={password} name="password" />
                    <TextField fullWidth variant="outlined" margin="normal" type="password" label="Confirm Password*" onChange={changeHandler} value={confirmPassword} name="confirmPassword" />

                    <Button variant="contained" color="primary" type="submit" style={{ marginTop: "20px" }}>Update Details</Button>
                </form>
                <NavLink to="/dashboard" style={{ color: '#56CEDB', textDecoration: 'none', marginTop: '20px', display: 'block' }}>
                    Return without changing anything.
                </NavLink>
            </Container>
            {x === 1 ? <Navigate to="/login" /> : null}
        </div>
    );
};

export default EditProfile;
