import React, { useState, useEffect } from "react";
import { Navigate } from "react-router-dom";
import axios from "axios";
import { Card, CardContent, Button, TextField } from "@material-ui/core";
import Header from "./Header";
import "./bg.css";

const TextbookSearch = () => {
  const [search, setSearch] = useState("");
  const [data, setData] = useState([]);
  const [tokenn, setTokenn] = useState(localStorage.getItem("token"));
  const [showRedirectButton, setShowRedirectButton] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost:5000/getbooks")
      .then((res) => {
        setData(res.data);
        setShowRedirectButton(res.data.length === 0);
      })
      .catch((error) => {
        console.error("Error fetching book data:", error);
      });
  }, []);

  const handleSearchChange = (e) => {
    setSearch(e.target.value);
  };

  const handleRedirect = () => {
    window.location.href = "http://localhost:5173";
  };

  return (
    <div className="first">
      <Header />

      <section className="container">
        <center>
          <h1 className="large" style={{ color: "#30332E", marginTop: "20px" }}>
            Textbook Search
          </h1>
        </center>

        <nav className="navbar navbar-light">
          <div className="container-fluid d-flex justify-content-between">
            <h3 className="navbar-brand">Search for Textbooks</h3>
            <TextField
              variant="outlined"
              placeholder="Search by title, author, or ISBN"
              value={search}
              onChange={handleSearchChange}
            />
          </div>
        </nav>

        {showRedirectButton && (
          <center>
            <Button
              variant="contained"
              color="primary"
              onClick={handleRedirect}
              style={{ marginBottom: "20px" }}
            >
              Visit Online Bookstore
            </Button>
          </center>
        )}

        <div className="Textbooks">
          <div className="row">
            {data.length >= 1 &&
              data
                .filter(
                  (books) =>
                    books.title
                      .toLowerCase()
                      .includes(search.toLowerCase()) ||
                    books.author
                      .toLowerCase()
                      .includes(search.toLowerCase()) ||
                    books.isbn.toLowerCase().includes(search.toLowerCase())
                )
                .map((books) => (
                  <div className="col-md-4">
                    <Card
                      className="Textbooks bg-light card"
                      style={{ margin: "10px", width: "25.5rem" }}
                    >
                      <CardContent>
                        <center>
                          <div>
                            <h2 style={{ color: "#6DAFED" }}>
                              Title: {books.title}
                            </h2>
                            <br />
                            <h6>Author: {books.author}</h6>
                            <br />
                            <h6>ISBN: {books.isbn}</h6>
                            <br />
                            <h6>
                              Location: {books.location || books.bookstore}
                            </h6>
                            <br />
                          </div>
                        </center>
                      </CardContent>
                    </Card>
                  </div>
                ))}
          </div>
        </div>
        <br />
        <br />
        <br />
        <br />
      </section>

      {tokenn === "undefined" && <Navigate to="/login" />}
    </div>
  );
};

export default TextbookSearch;
