import React, { useState } from 'react';
import axios from 'axios';
import { TextField, Button, Typography, List, ListItem, ListItemText } from '@mui/material';

const Search = () => {
  const [searchCriteria, setSearchCriteria] = useState({
    department: '',
    firstName: '',
    lastName: ''
  });
  const [searchResults, setSearchResults] = useState([]);

  const handleInputChange = (e) => {
    setSearchCriteria({
      ...searchCriteria,
      [e.target.name]: e.target.value
    });
  };

  const handleSearch = async () => {
    try {
      const response = await axios.get('http://localhost:5000/search', {
        params: searchCriteria
      });
      setSearchResults(response.data);
    } catch (error) {
      console.error('Error searching users:', error);
    }
  };

  return (
    <div>
      <Typography variant="h2">Search Users</Typography>
      <div>
        <TextField
          label="Department"
          variant="outlined"
          name="department"
          value={searchCriteria.department}
          onChange={handleInputChange}
        />
        <TextField
          label="First Name"
          variant="outlined"
          name="firstName"
          value={searchCriteria.firstName}
          onChange={handleInputChange}
        />
        <TextField
          label="Last Name"
          variant="outlined"
          name="lastName"
          value={searchCriteria.lastName}
          onChange={handleInputChange}
        />
        <Button variant="contained" onClick={handleSearch}>Search</Button>
      </div>
      <div>
        <Typography variant="h3">Search Results:</Typography>
        <List>
          {searchResults.map((user) => (
            <ListItem key={user._id}>
              <ListItemText primary={`${user.firstName} ${user.lastName}`} secondary={`${user.department} - ${user.phoneNumber} - ${user.email}`} />
            </ListItem>
          ))}
        </List>
      </div>
    </div>
  );
};

export default Search;
