// Forgot.js

import React, { useState } from "react";
import axios from "axios";

const ForgotPassword = () => {
  const [data, setData] = useState({
    email: '',
  });
  const { email } = data;

  const changeHandler = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5000/sendLoginCredentials", data)
      .then((res) => {
        if (res.data.message === 'User not found') {
          alert("User not found. Please check your email and try again.");
        } else if (res.data.message === 'Login credentials sent to your email') {
          alert("Login credentials sent to your email");
        } else {
          alert("Error sending login credentials. Please try again later.");
        }
      })
      .catch((error) => {
        console.error('Error:', error);
        alert("Error sending login credentials. Please try again later.");
      });
  };

  return (
    <div>
      <h2>Enter your email to receive login credentials</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          value={email}
          onChange={changeHandler}
          placeholder="Enter your email"
          required
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default ForgotPassword;
