import React, { useState } from 'react';
import axios from 'axios';

const ForgotPassword = () => {
  const [email, setEmail] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/sendLoginCredentials', email);
      alert('Login credentials sent to your email');
    } catch (error) {
      console.error('Error:', error.response.data.message);
      alert('Error sending login credentials. Please try again later.');
    }
  };

  return (
    <div>
      <h2>Enter your email to receive login credentials</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Enter your email"
          
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default ForgotPassword;
