import React, { useState } from 'react';
import axios from 'axios';

function RoommateSearch() {
    const [searchCriteria, setSearchCriteria] = useState({
        moveInDate: '',
        gender: '',
        priceRange: ''
    });
    const [searchResults, setSearchResults] = useState([]);

    const handleChange = e => {
        setSearchCriteria({ ...searchCriteria, [e.target.name]: e.target.value });
    };

    const handleSubmit = async e => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:5000/roommates/search', searchCriteria);
            setSearchResults(res.data);
        } catch (err) {
            console.error('Error searching roommates:', err);
        }
    };

    return (
        <div>
            <h2>Find Roommate</h2>
            <form onSubmit={handleSubmit}>
                <input type="date" name="moveInDate" value={searchCriteria.moveInDate} onChange={handleChange} placeholder="Move-in Date" required />
                <select name="gender" value={searchCriteria.gender} onChange={handleChange} required>
                    <option value="">Select Gender</option>
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                </select>
                <input type="number" name="priceRange" value={searchCriteria.priceRange} onChange={handleChange} placeholder="Price Range" required />
                <button type="submit">Search</button>
            </form>
            <div>
                {searchResults.map((result, index) => (
                    <div key={index}>
                        <p>{result.firstName} {result.lastName}</p>
                        <p>Gender: {result.gender}</p>
                        <p>Move-in Date: {result.moveInDate}</p>
                        <p>Price Range: {result.priceRange}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default RoommateSearch;
